import React, { useState } from 'react';
import { addScholarship } from '../api-client.ts';
import FetchScholarshipsByGPA from './FetchScholarshipsByGPA';

const AddNewScholarship = ({ onSuccess }) => {
  const [showForm, setShowForm] = useState(false);
  const [scholarships, setScholarships] = useState([]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    const newScholarshipData = {
      scholarshipName: form.scholarshipName.value,
      link: form.link.value,
      gpa: form.gpa.value,
    };
    const newScholarship = await addScholarship(newScholarshipData);
    // if (newScholarship?.name) {
    //   form.reset();
    //   onSuccess(newScholarship);
    // }
  };

  return (
    <div className="add-new-scholarship">
      {!showForm && (
        <div className="link" onClick={() => setShowForm(true)}>
          Add New Scholarship
        </div>
      )}

      {showForm && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Scholarship Name"
            name="scholarshipName"
          />
          <input
            type="text"
            placeholder="Scholarship Link"
            name="link"
          />
          <textarea
            placeholder="GPA Requirement"
            name="gpa"
            rows={5}
          />
          <button type="submit">Submit</button>
        </form>
      )}

      <FetchScholarshipsByGPA setScholarships={setScholarships} />

      <div className="scholarship-list">
        {scholarships.length > 0 ? (
          scholarships.map((scholarship, index) => (
            <div key={index} className="scholarship-item">
              <div className="scholarship-name">{scholarship.scholarshipName}</div>
              <div className="scholar-link">
                <a href={scholarship.link} target="_blank" rel="noopener noreferrer">
                  {scholarship.link}
                </a>
              </div>
              <div className="scholarship-gpa">GPA Requirement: {scholarship.gpa}</div>
            </div>
          ))
        ) : (
          <p>No scholarships found</p>
        )}
      </div>
    </div>
  );
};

export default AddNewScholarship;
