import { useState } from "react";
import { addScholarship, fetchScholarshipByGPA } from "../api-client.ts";
import React from "react";

const AddNewScholarship = ({ onSuccess }) => {
  const [showForm, setShowForm] = useState(false);
  const [scholarships, setScholarships] = useState([]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    const newScholarshipData = {
      scholarshipName: form.scholarshipName.value,
      link: form.link.value,
      gpa: form.gpa.value,

    };
    const newScholarship = await addScholarship(newScholarshipData);
    // if (newScholarship?.name) {
    //   form.reset();
    //   onSuccess(newScholarship);
    // }
  };

  const handleFetchByGPA = async (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    const gpa = form.gpa.value;
    const scholarships = await fetchScholarshipByGPA(gpa);
    console.log(scholarships);
  };


  return (
    <div className="add-new-scholarship">
      {!showForm && (
        <div className="link" onClick={() => setShowForm(true)}>
          Add New Scholarship
        </div>
      )}

      {showForm && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="scholarship Name"
            name="scholarshipName"
          />
          <input
            type="text"
            placeholder="scholarship link"
            name="link"
          />
          <textarea
            placeholder="gpa requierment"
            name="gpa"
            rows={5}
          />
          <button type="submit">Sumbit</button>
        </form>
      )}

      <form onSubmit={handleFetchByGPA}>
        <input
          type="text"
          placeholder="Enter GPA to fetch scholarships"
          name="gpa"
        />
        <button type="submit">Fetch Scholarships</button>
      </form>
    </div>
  );
};

export default AddNewScholarship;
